<script>
import { mapState, mapActions } from 'vuex'


export default {
  name: 'MyPage',
  methods: {
    ...mapActions([
      'getReviews',
      'deleteReview',
      'createReview',
      'editReview',
      'getReview',
      'getUserInfomation',
    ]),
  },
  computed: {
    ...mapState([
      'reviews',
      'username',
      'userid'
    ])
  },
  created: function () {
    if (this.$store.getters.isLoggedIn) {
      this.getReviews()
      this.getUserInfomation()
    } else {
      this.$router.push({ name: 'Login'})
    }
  },
}
</script>

